import com.ejemplo.dao.VideoDAO;
import com.ejemplo.model.Video;

import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.*;
import java.io.IOException;
import java.util.List;

@WebServlet("/videos")
public class VideoServlet extends HttpServlet {

    private final VideoDAO dao = new VideoDAO();

    @Override
    protected void doGet(HttpServletRequest req, HttpServletResponse resp)
            throws ServletException, IOException {

        String action = req.getParameter("action");
        String idParam = req.getParameter("id");

        if (action != null && idParam != null) {
            int id = Integer.parseInt(idParam);
            switch (action) {
                case "delete":
                    dao.eliminar(id);
                    break;
                case "like":
                    dao.toggleLike(id);
                    break;
            }
            resp.sendRedirect("videos");
            return;
        }

        // Listar todos los videos
        List<Video> lista = dao.listar();
        req.setAttribute("videos", lista);
        req.getRequestDispatcher("videos.jsp").forward(req, resp);
    }

    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp)
            throws ServletException, IOException {

        String nombre = req.getParameter("nombre");
        String link = req.getParameter("link");

        if (nombre != null && link != null && !nombre.isEmpty() && !link.isEmpty()) {
            Video v = new Video(nombre, link);
            dao.agregar(v);
        }

        resp.sendRedirect("videos");
    }
